function FullNameEditableInputCell(props: CellContext<any, any>) {
  const { table, getValue } = props;
  const initialValue = getValue();
  // We need to keep and update the state of the cell normally
  const [value, setValue] = React.useState(initialValue);

  const validate = (
    params: CellContext<Person, string> & { value: string }
  ) => {
    // È™åËØÅ
    if (params.value.trim() === "") {
      return "ÂßìÂêç‰∏çËÉΩ‰∏∫Á©∫";
    }
    return "";
  };

  // If the initialValue is changed external, sync it up with our state
  React.useEffect(() => {
    setValue(initialValue);
  }, [initialValue]);

  const onBlur = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.currentTarget.value;
    table.options.meta?.changeRow?.({
      ...props,
      value: newValue,
      validate,
    });
  };

  return (
    <TextInput
      variant="ghost"
      value={value}
      onChange={(e) => setValue(e.currentTarget.value)}
      onClick={(ev) => ev.stopPropagation()}
      onBlur={onBlur}
    />
  );
}

function AgeEditableInputWithValidateCell(props: CellContext<any, any>) {
  const { table, getValue } = props;
  const initialValue = getValue();
  // We need to keep and update the state of the cell normally
  const [value, setValue] = React.useState(initialValue);

  const validate = (
    params: CellContext<Person, string> & { value: string }
  ) => {
    // È™åËØÅ
    if (Number(params.value) > 200) {
      return "ÂÄº‰∏çËÉΩÂ§ß‰∫é200";
    }
    return "";
  };

  // When the input is blurred, we'll call our table meta's updateData function
  const onBlur = (ev: React.FocusEvent<HTMLInputElement>) => {
    const newValue = ev.target.value;
    // Â¶ÇÊûúÈ™åËØÅÈÄöËøáÂàôÂä†ÂÖ•ÂèòÊõ¥ÈõÜ,Âê¶ÂàôÂä†ÂÖ•ÈîôËØØÈõÜ
    table.options.meta?.changeRow?.({
      ...props,
      value: newValue,
      validate,
    });
  };

  // If the initialValue is changed external, sync it up with our state
  React.useEffect(() => {
    setValue(initialValue);
  }, [initialValue]);

  return (
    <NumberInput
